//import logo from './logo.svg';
import './App.css';
import { Button } from "baseui/button";

function App() {
return (
    <div className="App">
      <header className="App-header">
        <img src="https://rm7ix2r8hzo4121x321k81m1-wpengine.netdna-ssl.com/wp-content/uploads/2020/08/Children-and-Water-Content-1024x536.jpg" className="App-logo" alt="logo" />
        <h1>
          Drink water!!
        </h1>
    <Button
      onClick={() => clockStart()}
      overrides={{
        BaseButton: {
          style: ({ $theme }) => ({
            outline: `${$theme.colors.warning600} solid`,
            backgroundColor: $theme.colors.warning600
          })
        }
      }}
    >
      Click to start
    </Button>
      </header>
    </div>
  );
}
function clockStart() {
  setInterval(function() {
             date = new Date()
             let hour = date.getHours();
             let minutes = date.getMinutes();
             let seconds = date.getSeconds();
             document.getElementById("demo").innerHTML = hour + ":"+ minutes + ":" + seconds;
}, 1000);
}

export default App;
